import React, { useEffect, useState } from "react";
import roomData from "./rooms.json"; // JSON contains room data

const Room = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState({});
  const rooms = roomData;

  useEffect(() => {
    const intervals = {};

    rooms.forEach((room) => {
      intervals[room.id] = setInterval(() => {
        setCurrentImageIndex((prev) => ({
          ...prev,
          [room.id]: (prev[room.id] + 1) % room.images.length || 0,
        }));
      }, 1000);
    });

    return () => Object.values(intervals).forEach(clearInterval);
  }, [rooms]);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Rooms</h1>
      {rooms.map((room) => (
        <div
          key={room.id}
          className="flex border p-4 rounded-lg mb-6 shadow-md"
        >
          <div className="w-1/2 text-xl font-semibold text-gray-800">
            Room {room.id}: â‚¹{room.price} / night
          </div>
          <div className="w-1/2">
            <img
              src={room.images[currentImageIndex[room.id] || 0]}
              alt={`Room ${room.id}`}
              className="w-full h-64 object-cover rounded-md transition-all duration-300"
            />
          </div>
        </div>
      ))}
    </div>
  );
};

export default Room;
