import React, { useState, useEffect } from "react";
import Navbar from "./components/Navbar";

const images = [
  "/images/image1.jpg",
  "/images/image2.jpg",
  "/images/image3.jpg",
  "/images/image4.jpg",
  "/images/image5.jpg",
];

function App() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [position, setPosition] = useState({ top: "40%", left: "30%" });

  useEffect(() => {
    // Switch images every 3 seconds
    const imageInterval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);

    // Move text every 2 seconds to a random position
    const moveInterval = setInterval(() => {
      const top = Math.floor(Math.random() * 70) + "%";
      const left = Math.floor(Math.random() * 80) + "%";
      setPosition({ top, left });
    }, 2000);

    return () => {
      clearInterval(imageInterval);
      clearInterval(moveInterval);
    };
  }, []);

  return (
    <div className="bg-gray-50 min-h-screen">
      <Navbar />

      <div
        className="relative p-6 shadow-lg rounded-xl mt-20"
        style={{
          backgroundImage: `url(${images[currentIndex]})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "75vh",
          overflow: "hidden",
        }}
      >
        <h1
          className="text-3xl md:text-5xl font-bold text-white absolute transition-all duration-700 ease-in-out"
          style={{ top: position.top, left: position.left }}
        >
          Welcome to The Himalayan Vista Guest House
        </h1>
      </div>

      <div className="p-4 mt-4">
        <p className="text-gray-600 max-w-xl">
          Click{" "}
          <button
            onClick={() => window.open("https://www.google.com", "_blank")}
            className="ml-2 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
          >
            Rent now
          </button>{" "}
          for instant booking.
        </p>
      </div>
    </div>
  );
}

export default App;
