import React, { useState } from "react";
import { Link } from "react-router-dom";
const Navbar = () => {
  const [open, setOpen] = useState(false);
    const navLinkClasses =
  "px-4 py-2 border border-black bg-white text-black hover:bg-black hover:text-white transition-all duration-300 rounded transform hover:scale-110";


  return (
    <nav style={{ backgroundColor: "#d7dde4" }} className="shadow-md fixed w-full z-10 top-0">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          <div className="text-3xl font-bold px-4 py-2 text-[#242628]">
            The Himalayan Vista
          </div>

          <div className="md:hidden">
            <button onClick={() => setOpen(!open)} className="text-gray-700 text-2xl">
              â˜°
            </button>
          </div>

          <div className="hidden md:flex space-x-4">
            <a href="#" className={navLinkClasses}>Home</a>
            <a href="#" className={navLinkClasses}>Room</a>
            <a href="#" className={navLinkClasses}>Full Personal Space</a>
            <a href="#" className={navLinkClasses}>Bike Rental</a>
            <a href="#" className={navLinkClasses}>Explore Himachal</a>
            <a href="#" className={navLinkClasses}>Contact Us</a>
          </div>
        </div>

        {open && (
          <div className="md:hidden flex flex-col space-y-2 mt-2">
            <a href="#" className={navLinkClasses}>Home</a>
            <a href="#" className={navLinkClasses}>Room</a>
            <a href="#" className={navLinkClasses}>Full Personal Space</a>
            <a href="#" className={navLinkClasses}>Bike Rental</a>
            <a href="#" className={navLinkClasses}>Explore Himachal</a>
            <a href="#" className={navLinkClasses}>Contact Us</a>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
