import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar";

function App() {
  const images = [
    "/img1.jpg", // Replace with actual paths in /public or import them
    "/img2.jpg",
    "/img3.jpg",
    "/img4.jpg",
    "/img5.jpg",
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % images.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="bg-gray-50 min-h-screen">
      <Navbar />
      <div className="pt-20 px-4">
        <div
          className="p-8 shadow-lg text-center rounded-xl mb-6"
          style={{
            backgroundImage: `url(${images[currentIndex]})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            transition: "background-image 1s ease-in-out",
            minHeight: "300px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <h1 className="text-3xl md:text-5xl font-bold text-red-400 bg-white/70 px-6 py-4 rounded">
            Welcome to The Himalayan Vista Guest House
          </h1>
        </div>

        <div className="p-4 shadow rounded bg-white max-w-xl">
          <p className="text-gray-700">
            Click
            <button
              onClick={() => window.open("https://www.google.com", "_blank")}
              className="ml-2 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
            >
              Rent now
            </button>
            for instant booking.
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
