import React, { useState } from "react";

// ðŸŽ¨ Custom styles
const NAVBAR_STYLE = { backgroundColor: "#ff3322" }; // âœ… Use a valid 6-digit hex color
const TEXT_COLOR = "text-white"; // Optional: change to white text for contrast
const HOVER_COLOR = "hover:text-gray-300";
const BRAND_COLOR = "text-white";

const Navbar = () => {
  const [open, setOpen] = useState(false);

  return (
    <nav style={NAVBAR_STYLE} className={`shadow-md fixed w-full z-10 top-0`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          <div className={`text-xl font-bold ${BRAND_COLOR}`}>The Himalayan Vista</div>

          <div className="md:hidden">
            <button onClick={() => setOpen(!open)} className={`${TEXT_COLOR}`}>
              â˜°
            </button>
          </div>

          <div className="hidden md:flex space-x-4">
            <a href="#" className={`${TEXT_COLOR} ${HOVER_COLOR}`}>Home</a>
            <a href="#" className={`${TEXT_COLOR} ${HOVER_COLOR}`}>Room</a>
            <a href="#" className={`${TEXT_COLOR} ${HOVER_COLOR}`}>Full Personal Space</a>
            <a href="#" className={`${TEXT_COLOR} ${HOVER_COLOR}`}>Bike Rental</a>
            <a href="#" className={`${TEXT_COLOR} ${HOVER_COLOR}`}>Explore Himachal</a>
            <a href="#" className={`${TEXT_COLOR} ${HOVER_COLOR}`}>Contact Us</a>
          </div>
        </div>

        {open && (
          <div className="md:hidden flex flex-col space-y-2 mt-2">
            <a href="#" className={`${TEXT_COLOR} ${HOVER_COLOR}`}>Home</a>
            <a href="#" className={`${TEXT_COLOR} ${HOVER_COLOR}`}>Room</a>
            <a href="#" className={`${TEXT_COLOR} ${HOVER_COLOR}`}>Full Personal Space</a>
            <a href="#" className={`${TEXT_COLOR} ${HOVER_COLOR}`}>Bike Rental</a>
            <a href="#" className={`${TEXT_COLOR} ${HOVER_COLOR}`}>Explore Himachal</a>
            <a href="#" className={`${TEXT_COLOR} ${HOVER_COLOR}`}>Contact Us</a>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
